import React, {useState} from 'react'

const App = () => {
    
    const [serverData, setServerData] = useState();

    function fetchUsers() {
        fetch("/api/getUsers")
            .then(response => response.json()
                .then(data => setServerData(data)));
    }

    function createUser(event) {
        event.preventDefault();
        fetch("/api/createUser", { method: "POST" });
    }

  return (
    <>
          <h1>Sending / receiving from server</h1>
          <form onSubmit={createUser}>
            <input type="text" name="username" id="username" placeholder='username'/>
            <input type="text" name="note" id="note" placeholder='note' />
            <button>Create User!</button>
          </form>
          <button onClick={fetchUsers}>Fetch users from DB!</button>

          {serverData && serverData.map((user, idx) => {
              return <div key={idx}>Name: {user.name} Note: { user.note }</div>
          })}
    </>
  )
}

export default App
